import React, { useState, useEffect } from 'react';
import { Form, FormGroup, Input, Label } from "reactstrap";
import * as Yup from "yup";

import "./HamurSec.css"

const initialFormData = {
  kalinlik: "", // dropdown seçeneği için boş değer
};

function HamurSec({ direction, ...args }) {
  const [formData, setFormData] = useState(initialFormData);
  const [isValid, setIsValid] = useState(false);

  // Hata durumlarını saklamak için errors objesi
  const [errors, setErrors] = useState({
    kalinlik: "",
  });

  const formSchema = Yup.object().shape({
    kalinlik: Yup.string()
      .oneOf(["İnce", "Kalın"], "Lütfen bir boyut seçin")
      .required("Boyut seçimi zorunlu"),
  });

  useEffect(() => {
    formSchema.isValid(formData).then((valid) => setIsValid(valid));
  }, [formData]);

  function handleChange(event) {
    const name = event.target.name;
    const value = event.target.value;

    formSchema
      .validateAt(name, { [name]: value })
      .then(() => {
        setErrors({ ...errors, [name]: "" });
      })
      .catch((err) => {
        setErrors({ ...errors, [name]: err.message });
      });

    setFormData({ ...formData, [name]: value });
  }

  return (
    <>
      <div className='hamur-sec'>
        <h5>Hamur Seç <span className="red-star">*</span></h5>
        {errors.kalinlik && <div className="error" style={{ color: "red" }}>{errors.kalinlik}</div>}
        <select name="kalinlik" value={formData.kalinlik} onChange={handleChange}>
          <option value="">Seçiniz</option>
          <option value="İnce">İnce</option>
          <option value="Kalın">Kalın</option>
        </select>
      </div>
    </>
  );
}


export default HamurSec;
