import { Form, FormGroup, Input, Label } from "reactstrap"
import "./BoyutSec.css"
import * as Yup from "yup";

function BoyutSec() {
    const boyutlar = ["Küçük", "Orta", "Büyük"];

    const formSchema = Yup.object().shape({
        radioGroup: Yup
          .boolean()
          .required("You must choose")
          // required isn't required for checkboxes.
      });

    const [formData, setFormData] = useState(formSchema);
    const [isValid, setIsValid] = useState(false);
    const [errors, setErrors] = useState({
        radioGroup: ""
    });

  return (
    <>
    <div className="boyut-sec">
        <h5>Boyut Seç <span class="red-star">*</span></h5>
        <Form >
            {boyutlar.map((boyut, index) => (
                <FormGroup check key={index}>
                <Label check>
                    <Input type="radio" name="radioGroup" value={boyut} /> {boyut}
                </Label>
                </FormGroup>
            ))}
        </Form>
    </div>
    </>
  );
}

export default BoyutSec;
