import { Form, FormGroup, Input, Label } from "reactstrap";
import "./BoyutSec.css";
import { useState, useEffect } from "react";
import * as Yup from "yup";


function BoyutSec({formData, setFormData}) {
    const boyutlar = ["Küçük", "Orta", "Büyük"];

    const [isValid, setIsValid] = useState(false);

    // Hata durumlarını saklamak için errors objesi
    const [errors, setErrors] = useState({
        radioGroup: "",
    });

    const formSchema = Yup.object().shape({
        radioGroup: Yup.string()
            .oneOf(["Küçük", "Orta", "Büyük"], "Lütfen bir boyut seçin")
            .required("Boyut seçimi zorunlu"),
    });

    useEffect(() => {
        // Hata mesajını sayfa yüklendiğinde de göster
        formSchema.isValid(formData).then((valid) => {
          setIsValid(valid);
          if (!valid) {
            setErrors({ ...errors, radioGroup: "Bir boyut seçmelisiniz!" });
          }
        });
      }, [formData]);

    function handleChange(event) {
        const name = event.target.name;
        const value = event.target.value;
        const newFormData = { ...formData, [name]: value };

        formSchema
            .validateAt(name, newFormData)
            .then(() => {
                setErrors({ ...errors, [name]: "" });
            })
            .catch((err) => {
                setErrors({ ...errors, [name]: err.message });
            });

        setFormData(newFormData);
    }

    return (
        <>
            <div className="boyut-sec">
                <h5>Boyut Seç <span className="red-star">*</span></h5>
                {errors.radioGroup && <div className="error" style={{color:"red"}}>{errors.radioGroup}</div>}
                <Form>
                    {boyutlar.map((boyut, index) => (
                        <FormGroup check key={index}>
                            <Label check>
                                <Input
                                    type="radio"
                                    name="radioGroup"
                                    value={boyut}
                                    checked={formData.radioGroup === boyut}
                                    onChange={handleChange}
                                />{" "}
                                {boyut}
                            </Label>
                        </FormGroup>
                    ))}
                </Form>
            </div>
        </>
    );
}

export default BoyutSec;
