import React, { useState } from "react";
import { Checkbox } from "react-control-library";
import { Input, Form, Label, FormGroup } from "reactstrap";
import "./EkMalzemeler.css"

export default function App() {
  const [Pepperoni, setPepperoni] = React.useState(true);
  const [Sosis, setSosis] = React.useState(false);
  const [KanadaJambonu, setKanadaJambonu] = React.useState(false);
  const [TavukIzgara, setTavukIzgara] = React.useState(false);
  const [Sogan, setSogan] = React.useState(false);
  const [Domates, setDomates] = React.useState(false);
  const [Mısır, setMısır] = React.useState(false);
  const [Sucuk, setSucuk] = React.useState(false);
  const [Jalepino, setJalepino] = React.useState(false);
  const [Sarımsak, setSarımsak] = React.useState(false);
  const [Biber, setBiber] = React.useState(false);
  const [Ananas, setAnanas] = React.useState(false);
  const [Kabak, setKabak] = React.useState(false);

  const [selectedCount, setSelectedCount] = useState(0);

  const handleCheckboxChange = () => {
    // Burada seçilen checkbox'ın durumunu güncelleyin
    // Aynı zamanda seçilen checkbox sayısını kontrol edin ve istediğiniz sınıra ulaşıp ulaşmadığınızı kontrol edin
    // Örneğin, 5 checkbox seçme sınırı koymak istiyorsanız:
    if (selectedCount < 5) {
      setSelectedCount(selectedCount + 1);
    } else {
      // Seçim sınırına ulaşıldığında işlem yapmayın veya kullanıcıya bir uyarı gösterin
    }
  };

  return (
    <>
    <h4>Ek Malzemeler</h4>
    <p>En Fazla 10 Malzeme seçebilirsiniz 5TL</p>

    <Form>
            <FormGroup check>
                <Label check>
                <Input type="checkbox" 
                onChange={handleCheckboxChange}
                disabled={selectedCount >= 5}/> 
                Pepperoni
                </Label>
            </FormGroup>
            <FormGroup check>
                <Label check>
                <Input type="checkbox" 
                onChange={handleCheckboxChange}
                disabled={selectedCount >= 5}/> 
                Sosis
                </Label>
            </FormGroup>
            <FormGroup check>
                <Label check>
                <Input type="checkbox" 
                onChange={handleCheckboxChange}
                disabled={selectedCount >= 5}/> 
                Kanada Jambonu
                </Label>
            </FormGroup>
            <FormGroup check>
                <Label check>
                <Input type="checkbox" 
                onChange={handleCheckboxChange}
                disabled={selectedCount >= 5}/> 
                Tavuk Izgara
                </Label>
            </FormGroup>
            <FormGroup check>
                <Label check>
                <Input type="checkbox" 
                onChange={handleCheckboxChange}
                disabled={selectedCount >= 5}/> 
                Soğan
                </Label>
            </FormGroup>
            <FormGroup check>
                <Label check>
                <Input type="checkbox" 
                onChange={handleCheckboxChange}
                disabled={selectedCount >= 5}/> 
                Domates
                </Label>
            </FormGroup>
            <FormGroup check>
                <Label check>
                <Input type="checkbox" 
                onChange={handleCheckboxChange}
                disabled={selectedCount >= 5}/> 
                Mısır
                </Label>
            </FormGroup>
            <FormGroup check>
                <Label check>
                <Input type="checkbox" 
                onChange={handleCheckboxChange}
                disabled={selectedCount >= 5}/> 
                Sucuk
                </Label>
            </FormGroup>
            <FormGroup check>
                <Label check>
                <Input type="checkbox" 
                onChange={handleCheckboxChange}
                disabled={selectedCount >= 5}/> 
                Jalepino
                </Label>
            </FormGroup>
            <FormGroup check>
                <Label check>
                <Input type="checkbox" 
                onChange={handleCheckboxChange}
                disabled={selectedCount >= 5}/> 
                Sarımsak
                </Label>
            </FormGroup>
            <FormGroup check>
                <Label check>
                <Input type="checkbox" 
                onChange={handleCheckboxChange}
                disabled={selectedCount >= 5}/> 
                Biber
                </Label>
            </FormGroup>
            <FormGroup check>
                <Label check>
                <Input type="checkbox" 
                onChange={handleCheckboxChange}
                disabled={selectedCount >= 5}/> 
                Ananas
                </Label>
            </FormGroup>
            <FormGroup check>
                <Label check>
                <Input type="checkbox" 
                onChange={handleCheckboxChange}
                disabled={selectedCount >= 5}/> 
                Kabak
                </Label>
            </FormGroup>
        </Form>
    </>
  );


}
