import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import * as Yup from "yup";

import "./HamurSec.css"

const initialFormData = {
    option: "", // Radio seçeneği için boş değer
};

function HamurSec({ direction, ...args }) {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggle = () => setDropdownOpen((prevState) => !prevState);


    const [formData, setFormData] = useState(initialFormData);
    const [isValid, setIsValid] = useState(false);

    // Hata durumlarını saklamak için errors objesi
    const [errors, setErrors] = useState({
        option: "",
    });

    const formSchema = Yup.object().shape({
        option: Yup.string()
            .oneOf(["İnce", "Kalın"], "Lütfen bir kalınlık seçin")
            .required("Kalınlık seçimi zorunlu"),
    });

    useEffect(() => {
        formSchema.isValid(formData).then((valid) => setIsValid(valid));
    }, [formData]);

    function handleChange(event) {
        const name = event.target.name;
        const value = event.target.value;
        const newFormData = { ...formData, [name]: value };

        formSchema
            .validateAt(name, newFormData)
            .then(() => {
                setErrors({ ...errors, [name]: "" });
            })
            .catch((err) => {
                setErrors({ ...errors, [name]: err.message });
            });

        setFormData(newFormData);
    }

  return (
    <>
    <div className='hamur-sec'>
      <h5>Hamur Seç <span class="red-star">*</span></h5>
      <select 
      checked={formData.option === boyut}
      onChange={handleChange}
      >
        {errors.radioGroup && <div className="error">{errors.radioGroup}</div>}
        {!formData.radioGroup && <span className="disabled" style={{ color: "red" }}>Bir boyut seçmelisiniz!</span>}
        <option value="lutfen-secin">Lütfen seçin</option>
        <option value="ince-hamur">İnce</option>
        <option value="kalin-hamur">Kalın</option>
      </select>
    </div>

    </>
  );
}

HamurSec.propTypes = {
  direction: PropTypes.string,
};

export default HamurSec;
