import { Form, FormGroup, Input, Label } from 'reactstrap';
import { useForm } from 'react-hook-form';
import * as Yup from 'yup';

function BoyutSec() {
  const boyutlar = ['Küçük', 'Orta', 'Büyük'];

  // Yup doğrulama şemasını oluşturun
  const boyutSecSchema = Yup.object().shape({
    radioGroup: Yup.string().required('Boyut seçimi zorunlu'),
  });

  const { register, handleSubmit, errors } = useForm({
    validationSchema: boyutSecSchema,
  });

  // Form gönderildiğinde yapılacak işlem
  const onSubmit = (data) => {
    console.log('Seçilen boyut:', data.radioGroup);
  };

  return (
    <>
      <div className="boyut-sec">
        <h5>Boyut Seç <span class="red-star">*</span></h5>
        <Form onSubmit={handleSubmit(onSubmit)}>
          {boyutlar.map((boyut, index) => (
            <FormGroup check key={index}>
              <Label check>
                <Input
                  type="radio"
                  name="radioGroup" // "radioGroup" olarak ayarlayın
                  value={boyut}
                  ref={register}
                /> {boyut}
              </Label>
            </FormGroup>
          ))}
          {errors.radioGroup && (
            <div className="error">{errors.radioGroup.message}</div>
          )}
          <button type="submit">Gönder</button>
        </Form>
      </div>
    </>
  );
}

export default BoyutSec;
