import React, { useState, useEffect } from 'react';
import { Form, FormGroup, Input, Label } from "reactstrap";
import * as Yup from "yup";

import {
  Dropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
} from 'reactstrap';
import PropTypes from 'prop-types';

import "./HamurSec.css"

const initialFormData = {
  select: "", // dropdown seçeneği için boş değer
};

function HamurSec({ direction, ...args }) {
  const kalinliklar = ["Lütfen Seçiniz","İnce", "Kalın"];

  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggle = () => setDropdownOpen((prevState) => !prevState);


  const [formData, setFormData] = useState(initialFormData);
  const [isValid, setIsValid] = useState(false);

  // Hata durumlarını saklamak için errors objesi
  const [errors, setErrors] = useState({
      select: "",
  });

  const formSchema = Yup.object().shape({
      select: Yup.string()
          .oneOf(["Küçük", "Orta", "Büyük"], "Lütfen bir boyut seçin")
          .required("Boyut seçimi zorunlu"),
  });

  useEffect(() => {
      formSchema.isValid(formData).then((valid) => setIsValid(valid));
  }, [formData]);

  function handleChange(event) {
      const name = event.target.name;
      const value = event.target.value;
      const newFormData = { ...formData, [name]: value };

      formSchema
          .validateAt(name, newFormData)
          .then(() => {
              setErrors({ ...errors, [name]: "" });
          })
          .catch((err) => {
              setErrors({ ...errors, [name]: err.message });
          });

      setFormData(newFormData);
  }

  return (
    <>
    <div className='hamur-sec'>
      <h5>Hamur Seç <span class="red-star">*</span></h5>

      <Form>
        <Label for="exampleSelect">
          Select
        </Label>
        <Input
          id="exampleSelect"
          name="select"
          type="select"
        >
          {kalinliklar.map((kalinlik, index) => (
            <option value={kalinlik}>{kalinlik}</option>
          ))}
        </Input>
      </Form>
    </div>



    </>
  );
}

HamurSec.propTypes = {
  direction: PropTypes.string,
};

export default HamurSec;
