import React, { useState } from 'react';
import { Form, FormGroup, Input, Label } from "reactstrap";

import {
  Dropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
} from 'reactstrap';
import PropTypes from 'prop-types';

import "./HamurSec.css"

function HamurSec({ direction, ...args }) {
  const kalinliklar = ["Lütfen Seçiniz","İnce", "Kalın"];

  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggle = () => setDropdownOpen((prevState) => !prevState);

  return (
    <>
    <div className='hamur-sec'>
      <h5>Hamur Seç <span class="red-star">*</span></h5>
      <select>
        <option value="ince-hamur">İnce</option>
        <option value="kalin-hamur">Kalın</option>
      </select>

      <Form>
        <Label for="exampleSelect">
          Select
        </Label>
        <Input
          id="exampleSelect"
          name="select"
          type="select"
        >
          {kalinliklar.map((kalinlik, index) => (
            <option value={kalinlik}>{kalinlik}</option>
          ))}
        </Input>
      </Form>
    </div>



    </>
  );
}

HamurSec.propTypes = {
  direction: PropTypes.string,
};

export default HamurSec;
