import React, { useState, useEffect } from 'react';
import { Form, FormGroup, Input, Label } from "reactstrap";

import PropTypes from 'prop-types';
import * as Yup from "yup";

import "./HamurSec.css"

const initialFormData = {
    option: "", // Radio seçeneği için boş değer
};

function HamurSec({ direction, ...args }) {
    const kalinliklar = ["İnce", "Kalın"];

  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggle = () => setDropdownOpen((prevState) => !prevState);


    const [formData, setFormData] = useState(initialFormData);
    const [isValid, setIsValid] = useState(false);

    // Hata durumlarını saklamak için errors objesi
    const [errors, setErrors] = useState({
        option: "",
    });

    const formSchema = Yup.object().shape({
        option: Yup.string()
            .oneOf(["İnce", "Kalın"], "Lütfen bir kalınlık seçin")
            .required("Kalınlık seçimi zorunlu"),
    });

    useEffect(() => {
        formSchema.isValid(formData).then((valid) => setIsValid(valid));
    }, [formData]);

    function handleChange(event) {
        const name = event.target.name;
        const value = event.target.value;
        const newFormData = { ...formData, [name]: value };

        formSchema
            .validateAt(name, newFormData)
            .then(() => {
                setErrors({ ...errors, [name]: "" });
            })
            .catch((err) => {
                setErrors({ ...errors, [name]: err.message });
            });

        setFormData(newFormData);
    }

  return (
    <>
    <div className='hamur-sec'>
      <h5>Hamur Seç <span class="red-star">*</span></h5>
      <Form>
            {kalinliklar.map((kalinlik, index) => (
                <FormGroup check key={index}>
                    <Label check>
                        <Input
                            type="dropdown"
                            name="dropdownMenu"
                            value={kalinlik}
                            checked={formData.radioGroup === kalinlik}
                            onChange={handleChange}
                        />{" "}
                        {kalinlik}
                    </Label>
                </FormGroup>
            ))}
        </Form>
    </div>

    </>
  );
}

HamurSec.propTypes = {
  direction: PropTypes.string,
};

export default HamurSec;
