import { Form, FormGroup, Input, Label } from "reactstrap"
import "./BoyutSec.css"
import * as yup from "yup";


function BoyutSec() {
    const boyutlar = ["Küçük", "Orta", "Büyük"];
    const boyutSchema = yup.object().shape({
        radioGroup: yup.string().required(),
      });
      const handleSubmit = (e) => {
        e.preventDefault();

        const formData = e.target.form.toJSON();

        const validationErrors = boyutSchema.validate(formData);

        if (validationErrors.length > 0) {
          alert(validationErrors[0].message);
          return;
        }

        // Form verileri doğru ise burada işlem yapabilirsiniz.
      };
      return (
        <>
        <div className="boyut-sec">
            <h5>Boyut Seç <span class="red-star">*</span></h5>
            <Form onSubmit={handleSubmit}>
                {boyutlar.map((boyut, index) => (
                    <FormGroup check key={index}>
                    <Label check>
                        <Input type="radio" name="radioGroup" value={boyut} /> {boyut}
                    </Label>
                    </FormGroup>
                ))}
                <button type="submit">Gönder</button>
            </Form>
        </div>
        </>
      );


}

export default BoyutSec;
