import { Form, FormGroup, Input, Label } from "reactstrap";
import "./BoyutSec.css";
import { useState, useEffect } from "react";
import * as Yup from "yup";

const initialFormData = {
    radioGroup: "", // Radio seçeneği için boş değer
};

function BoyutSec() {
    const boyutlar = ["Küçük", "Orta", "Büyük"];

    const [formData, setFormData] = useState(initialFormData);

    // Hata durumlarını saklamak için errors objesi
    const [errors, setErrors] = useState({
        radioGroup: "",
    });

    const formSchema = Yup.object().shape({
        radioGroup: Yup.string()
            .oneOf(["Küçük", "Orta", "Büyük"], "Lütfen bir boyut seçin")
            .required("Boyut seçimi zorunlu"),
    });

    useEffect(() => {
        formSchema.isValid(formData).then((valid) => {
            setErrors({ radioGroup: "" }); // Her kontrolde hatayı temizle
            setFormData({ ...formData, radioGroup: formData.radioGroup }); // FormData'yı güncelle
        });
    }, [formData]);

    function handleChange(event) {
        const name = event.target.name;
        const value = event.target.value;
        const newFormData = { ...formData, [name]: value };

        setFormData(newFormData);
    }

    return (
        <>
            <div className="boyut-sec">
                <h5>Boyut Seç <span className="red-star">*</span></h5>
                <Form>
                    {boyutlar.map((boyut, index) => (
                        <FormGroup check key={index}>
                            <Label check>
                                <Input
                                    type="radio"
                                    name="radioGroup"
                                    value={boyut}
                                    checked={formData.radioGroup === boyut}
                                    onChange={handleChange}
                                />{" "}
                                {boyut}
                            </Label>
                        </FormGroup>
                    ))}
                    {errors.radioGroup && <div className="error">{errors.radioGroup}</div>}
                    {!formData.radioGroup && (
                        <span data-cy="submit-form-button">
                            Kayıt
                        </span>
                    )}
                </Form>
            </div>
        </>
    );
}

export default BoyutSec;
