import { Card, CardTitle, CardText, Button } from "reactstrap";

function SiparisToplami({ selectedItems }) {
  return (
    <Card
        body
        className="text-center"
        style={{
        width: '18rem'
        }}
    >
        <CardTitle tag="h5">
        Sipariş Toplamı
        </CardTitle>
        <CardText>
        Seçimler {selectedItems.length * 5} TRY
        </CardText>
        <CardText>
        Toplam  
        </CardText>
        <Button color="primary">
        SİPARİŞ VER
        </Button>
    </Card>
  );
}

export default SiparisToplami;
