import { Form, FormGroup, Input, Label } from "reactstrap"
import "./BoyutSec.css"
import { useState, useEffect } from "react";
import * as Yup from "yup";


const initialFormData = {

    radioGroup: "", //radio

  };
function BoyutSec() {
    const boyutlar = ["Küçük", "Orta", "Büyük"];

    const [formData, setFormData] = useState(initialFormData);
    const [isValid, setIsValid] = useState(false);
    const [errors, setErrors] = useState({
        radioGroup: "",
    });

    const formSchema = Yup.object().shape({

        position: Yup.string()
          .oneOf(["Küçük", "Orta", "Büyük"], "Must select one of the positions")
          .required("Position is Required"),
      });

      useEffect(() => {
        formSchema.isValid(formData).then((valid) => setIsValid(valid));
      }, [formData]);
      function handleChange(event) {
        const name = event.target.name;
        const value =
          event.target.type === "checkbox" //koşul
            ? event.target.checked //true ise
            : event.target.value; //false ise
        const newFormData = { ...formData, [name]: value };

        Yup.reach(formSchema, name)
          .validate(value)
          .then((valid) => {
            setErrors({ ...errors, [name]: "" });
          })
          .catch((err) => {
            setErrors({ ...errors, [name]: err.errors[0] });
          });

        setFormData(newFormData);
      }
  return (
    <>
    <div className="boyut-sec">
        <h5>Boyut Seç <span class="red-star">*</span></h5>
        <Form >
            {boyutlar.map((boyut, index) => (
                <FormGroup check key={index}>
                <Label check>
                    <Input 
                    type="radio" 
                    name="radioGroup" 
                    value={boyut} 
                    checked={formData.position === "hr"}
                    onChange={handleChange}
                    /> {boyut}
                </Label>
                </FormGroup>
            ))}
        </Form>
    </div>
    </>
  );
}

export default BoyutSec;
