import React, { useState, useEffect } from 'react';
import { Form, FormGroup, Input, Label } from "reactstrap";
import * as Yup from "yup";

import {
  Dropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
} from 'reactstrap';
import PropTypes from 'prop-types';

import "./HamurSec.css"

const initialFormData = {
  kalinlik: "", // dropdown seçeneği için boş değer
};

function HamurSec({ direction, ...args }) {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggle = () => setDropdownOpen((prevState) => !prevState);


  const [formData, setFormData] = useState(initialFormData);
  const [isValid, setIsValid] = useState(false);

  // Hata durumlarını saklamak için errors objesi
  const [errors, setErrors] = useState({
      kalinlik: "",
  });

  const formSchema = Yup.object().shape({
      kalinlik: Yup.string()
          .oneOf(["İnce", "Kalın"], "Lütfen bir boyut seçin")
          .required("Boyut seçimi zorunlu"),
  });

  useEffect(() => {
      formSchema.isValid(formData).then((valid) => setIsValid(valid));
  }, [formData]);

  function handleChange(event) {
      const name = event.target.name;
      const value = event.target.value;
      const newFormData = { ...formData, [name]: value };

      formSchema
          .validateAt(name, newFormData)
          .then(() => {
              setErrors({ ...errors, [name]: "" });
          })
          .catch((err) => {
              setErrors({ ...errors, [name]: err.message });
          });

      setFormData(newFormData);
  }

  return (
    <>
    <div className='hamur-sec'>
      <h5>Hamur Seç <span class="red-star">*</span></h5>
      {errors.kalinlik && <div className="error">{errors.kalinlik}</div>}
      {!formData.kalinlik && <span className="disabled" style={{ color: "red" }}>Bir boyut seçmelisiniz!</span>}
      <select>
        <option value="">Seçiniz</option>
        <option value="İnce">İnce</option>
        <option value="Kalın">Kalın</option>
      </select>
    </div>



    </>
  );
}

HamurSec.propTypes = {
  direction: PropTypes.string,
};

export default HamurSec;
