import React from "react";
import { Checkbox } from "react-control-library";
import { Input } from "reactstrap";

export default function App() {
  const [Pepperoni, setPepperoni] = React.useState(true);
  const [Sosis, setSosis] = React.useState(false);
  const [KanadaJambonu, setKanadaJambonu] = React.useState(false);
  const [TavukIzgara, setTavukIzgara] = React.useState(false);
  const [Sogan, setSogan] = React.useState(false);
  const [Domates, setDomates] = React.useState(false);
  const [Mısır, setMısır] = React.useState(false);
  const [Sucuk, setSucuk] = React.useState(false);
  const [Jalepino, setJalepino] = React.useState(false);
  const [Sarımsak, setSarımsak] = React.useState(false);
  const [Biber, setBiber] = React.useState(false);
  const [Ananas, setAnanas] = React.useState(false);
  const [Kabak, setKabak] = React.useState(false);


  return (
    <>
    <h4>Ek Malzemeler</h4>
    <p>En Fazla 10 Malzeme seçebilirsiniz 5TL</p>
    <div id="checkbox" style={{ margin: "30px" }}>
      <Checkbox
        inputTag={Input}
        indeterminate={allInDiffState()}
        checked={ca && wa && ny && nc}
        onChange={(e) => selectAllEvent(e)}
      />{" "}
      Select All
      <br />
      <Checkbox
        inputTag={Input}
        checked={ca}
        onChange={(e) => setCa(e.target.checked)}
      />{" "}
      CA
      <br />
      <Checkbox
        inputTag={Input}
        checked={wa}
        onChange={(e) => setWa(e.target.checked)}
      />{" "}
      WA
      <br />
      <Checkbox
        inputTag={Input}
        checked={ny}
        onChange={(e) => setNy(e.target.checked)}
      />{" "}
      NY
      <br />
      <Checkbox
        inputTag={Input}
        checked={nc}
        onChange={(e) => setNc(e.target.checked)}
      />{" "}
      NC
      <br />
      {Things()}
    </div>
    </>
  );

  function allInDiffState() {
    if (ca && wa && ny && nc) return false;
    if (!ca && !wa && !ny && !nc) return false;
    return true;
  }

  function selectAllEvent(e) {
    setCa(e.target.checked);
    setWa(e.target.checked);
    setNy(e.target.checked);
    setNc(e.target.checked);
  }

  function boxStyle() {
    return {
      border: "1px grey dotted",
      padding: "2px 10px",
      margin: "10px 0px"
    };
  }

  function Things() {
    return (
      <div style={boxStyle()}>
        <p>
          <b>Things to Try</b>
        </p>
        <hr />
        <ul>
          <li>Attributes to look for</li>
          <ul>
            <li>indeterminate</li>
            <li>When checked, a class 'checkbox_checked' is applied</li>
            <li>
              When indeterminate, a class 'checkbox_indeterminate' is applied
            </li>
          </ul>
        </ul>
      </div>
    );
  }
}
