import { Card, CardTitle, CardText, Button } from "reactstrap";
import { useHistory } from "react-router-dom"; 

function SiparisToplami(props) {
  const history = useHistory(); 

  const SuccessPage = () => { 
    const dataToPass = {
      selectedItems: props.selectedItems,
      counter: props.counter,
      priceOfPizza: props.priceOfPizza,
      selectedBoyut: props.selectedBoyut,
      selectedHamur: props.selectedHamur
    };

    history.push({
      pathname: "/success",
      state: dataToPass,
    });    
  }

  // Seçilen boyutun değeri boşsa butonu etkisiz hale getir
  const isButtonDisabled = props.selectedBoyut === null;

  return (
    <Card
      body
      className="text-center"
      style={{
        width: '18rem'
      }}
    >
      <CardTitle tag="h5">
        Sipariş Toplamı
      </CardTitle>
      <CardText>
        Seçimler {props.selectedItems.length * 5} ₺
      </CardText>
      <CardText style={{ color: 'red' }}>
        Toplam {props.counter * (props.priceOfPizza + props.selectedItems.length * 5)} ₺
      </CardText>
      <Button color="warning" onClick={SuccessPage} disabled={isButtonDisabled}>
        SİPARİŞ VER {props.selectedBoyut}
      </Button>
    </Card>
  );
}

export default SiparisToplami;
