import { Card, CardTitle, CardText, Button } from "reactstrap";
import { redirect } from "react-router-dom";

function SiparisToplami(props) {
  const handleButtonClick = () => {
    // Burada başka bir sayfaya yönlendirme işlemi gerçekleştirilecek
    window.location.href = '/Success'; // Yönlendirmek istediğiniz URL'yi buraya ekleyin
  }

  return (
    <Card
        body
        className="text-center"
        style={{
        width: '18rem'
        }}
    >
        <CardTitle tag="h5">
        Sipariş Toplamı
        </CardTitle>
        <CardText>
        Seçimler {props.selectedItems.length * 5} ₺
        </CardText>
        <CardText style={{ color: 'red' }}>
        Toplam {props.counter * (props.priceOfPizza + props.selectedItems.length * 5)} ₺
        </CardText>
        <Button color="warning" onClick={this.handleButtonClick}>
        SİPARİŞ VER
        </Button>
    </Card>
  );
}

export default SiparisToplami;
