import { Form, FormGroup, Input, Label } from "reactstrap"
import "./BoyutSec.css"
import { useState, useEffect, formState } from "react-router-dom"; 
import {  setButtonDisabled } from "react";


import * as Yup from "yup";

function BoyutSec() {
    const boyutlar = ["Küçük", "Orta", "Büyük"];

    const formSchema = Yup.object().shape({
        radioGroup: Yup
          .boolean()
          .required("You must choose")
          // required isn't required for checkboxes.
      });

      const [errors, setErrors] = useState({
        radioGroup: ""
      });

      useEffect(() => {
        /* We pass the entire state into the entire schema, no need to use reach here.
        We want to make sure it is all valid before we allow a user to submit
        isValid comes from Yup directly */
        formSchema.isValid(formState).then(valid => {
          setButtonDisabled(!valid);
        });
      }, [formState]);



  return (
    <>
    <div className="boyut-sec">
        <h5>Boyut Seç <span class="red-star">*</span></h5>
        <Form >
            {boyutlar.map((boyut, index) => (
                <FormGroup check key={index}>
                <Label check>
                    <Input type="radio" name="radioGroup" value={boyut} /> {boyut}
                </Label>
                </FormGroup>
            ))}
        </Form>
    </div>
    </>
  );
}

export default BoyutSec;
